public class Main_6 {

	public static void main(String[] args) throws Exception {
		final Main_6 main = new Main_6();
		final String sequence = "";
		System.out.println(main.findFirstMarker(sequence));
		System.out.println(main.findFirstMessage(sequence));
	}

	final Map<String, Integer> examples = Map.of(
			"mjqjpqmgbljsphdztnvjfqwrcgsmlb", 7,
			"bvwbjplbgvbhsrlpgdmjqwftvncz", 5,
			"nppdvjthqldpwncqszvftbrmjlhg", 6,
			"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10,
			"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11
			);
	
	final Map<String, Integer> examples2 = Map.of(
			"mjqjpqmgbljsphdztnvjfqwrcgsmlb", 19,
			"bvwbjplbgvbhsrlpgdmjqwftvncz", 23,
			"nppdvjthqldpwncqszvftbrmjlhg", 23,
			"nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 29,
			"zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 26
	);
	
	@Test
	void firstMarkerTest() {
		examples.forEach((sequence, position) -> {
			assertEquals(position, findFirstMarker(sequence));
		});
	}
	
	@Test
	void firstMessageTest() {
		examples2.forEach((sequence, position) -> {
			assertEquals(position, findFirstMessage(sequence));
		});
	}
	
	private Integer findFirstMarker(String sequence) {
		for (int i=4; i < sequence.length(); i++) {
			if (hasRepeteatedChar(sequence.substring(i-4, i))) return i;
		}
		throw new IllegalArgumentException();
	}

	private Integer findFirstMessage(String sequence) {
		for (int i=14; i < sequence.length(); i++) {
			if (hasRepeteatedChar(sequence.substring(i-14, i))) return i;
		}
		throw new IllegalArgumentException();
	}

	private boolean hasRepeteatedChar(String string) {
		for (int i =0; i < string.length(); i++) {
			for (int j = i +1; j < string.length(); j++) {
				if (string.charAt(i) == string.charAt(j)) return false;
			}
		}
		return true;
	}
}
